    /**
     * 求最终位置
     */
    fun part1(input: String = puzzle): Int {
        var floor = 0
        input.forEach {
            if (it == '(') {
                floor += 1
            } else {
                floor -= 1
            }
        }
        return floor
    }

    /**
     * 求第一次为 -1 是的 position(从 1 开始)
     */
    fun part2(input: String = puzzle): Int {
        var floor = 0
        var position = 1
        for ((i, c) in input.withIndex()) {
            if (c == '(') floor++ else floor--
            if (floor == -1) {
                position += i
                break
            }
        }
        return position
    }
}

fun main() {
    NotQuiteLisp().run {
        part1().also { println("目标楼层为: $it") }
        part2().also { println("首次进入地下室的position为: $it") }
    }
}